mapper.select:
		SELECT COLUMN_NAME AS 'column', DATA_TYPE AS 'type', COLUMN_KEY AS 'key'
        FROM INFORMATION_SCHEMA.COLUMNS
        WHERE TABLE_SCHEMA='streettohome' AND TABLE_NAME=#{table};

SELECT COUNT(*) 
FROM member 
WHERE m_state = #{m_state} AND m_auth = 1;

mapper.update:
update board
set b_del = 1
where b_no = #{b_no}

update member
set m_state = 3
where m_no = #{m_no}

update ask
set ak_del =1
where ak_no = #{ak_no}

update banner
set ba_img1 = #{ba_img1}
where ba_no = #{ba_no};

update report
set rp_del = 1
where rp_no;
