mapper.insert:
INSERT INTO reply
(r_no, r_ctnt, r_idx, b_no, m_no)
VALUES
((SELECT NEXTVAL(reply_seq))
, #{r_ctnt}
, (SELECT LASTVAL(reply_seq))
, #{b_no}
, #{m_no}
)

INSERT INTO reply
(r_no, r_ctnt, r_idx, r_dept, r_ord,
b_no, m_no)
VALUES
((SELECT NEXTVAL(reply_seq))
, #{r_ctnt}
, #{r_idx}
, #{r_dept}+1
, #{r_ord}+1
, #{b_no}
, #{m_no}
)

mapper.update:
UPDATE reply
SET r_ord = r_ord+1
WHERE r_idx = #{r_idx} AND r_ord > #{r_ord}

UPDATE reply
SET r_del = 1
WHERE r_no = #{r_no}
